public class Part3 {
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int currStop = dna.indexOf(stopCodon, startIndex);
        while(currStop != -1){
            int diff = currStop - startIndex;
            if(diff % 3 == 0)
            {
                return currStop;
            }
            else
            {
                currStop = dna.indexOf(stopCodon, currStop + startIndex + 1);
            }
        }
        return dna.length();
    }
    
    
    
    public int countGenes(String dna){
        int result = 0;
        int startIndex = dna.indexOf("ATG");
        if(startIndex == -1){
                return result;
            }
        
        while(true){
            if(startIndex == -1){
                return result;
            }

            int taaIndex = findStopCodon(dna, startIndex, "TAA");
            int tagIndex = findStopCodon(dna, startIndex, "TAG");
            int tgaIndex = findStopCodon(dna, startIndex, "TGA");
            
            int stopIndex = Math.min(taaIndex, Math.min(tagIndex, tgaIndex));
            if(stopIndex == dna.length()){
                break;
            }
            result++;
            startIndex = dna.indexOf("ATG", stopIndex);
        }
        
        return result;
        
    }
    
    public void testCountGenes(){
        String dna = "";
        System.out.println(countGenes(dna));
    }
}
